unsigned short *levelVect[LEVELS] = { level1, level2 };
glm::ivec2 levelSizes[LEVELS] = { { 160, 40 }, {120, 50} };

void setupMap(MapData &md, int levelId)
{

	if(levelId == -2)
	{
		md.cleanup();
		return;
	}

	md.create(levelSizes[levelId].x, levelSizes[levelId].y, levelVect[levelId]);

	switch (levelId)
	{
	case 0:
	{
		md.signDataVector.emplace_back(glm::ivec2{ 29, 22 }, "You can grab on walls\nand jump from them");
		md.signDataVector.emplace_back(glm::ivec2{ 38, 17 }, "Hold jump longer\nto jump higher");
		md.signDataVector.emplace_back(glm::ivec2{ 73, 19 }, "Don't fall\ninto water");		
	
		md.exitDataVector.emplace_back(glm::ivec2{ 112, 26 }, 1);

		//md.torchDataVector.emplace_back(glm::ivec2{ 112, 26 }, 1);
	}
	break;
	case 1:
	{

		md.exitDataVector.emplace_back(glm::ivec2{ 17, 36 }, -2);

	}
	break;

	default:
		break;
	}

}

void saveState(glm::ivec2 playerSpawnPos, int levelId)
{

	if(levelId == -2)
	{
		std::ofstream f;
		f.open("state", std::ofstream::out | std::ofstream::trunc);
		f.close();
		return;
	}

	std::ofstream f("state");
	
	if (!f.is_open()) { return; }

	int posX = playerSpawnPos.x;
	int posY= playerSpawnPos.y;

	f << posX << " " << posY << " " << levelId;
		
	f.close();
}

bool loadLevelFromLastState(int &level, glm::ivec2 &spawn)
{
	std::ifstream f("state");

	if(!f.is_open() || f.eof())
	{
		level = -2;
		spawn = {};
		f.close();
		return 0;
	}else
	{
		f >> spawn.x >> spawn.y >> level;

		return 1;
	}

}